    , SortOrder
    , IsEnabled
    , CreatedOn
    , UpdatedOn
) VALUES (
    'Home'
    , '/home'
    , '<p>We hold to beliefs which reflect the simple teaching of Jesus Christ\r\n            and his Apostles. Our rule of life is to follow in the footsteps of\r\n            Jesus and his disciples.\r\n          <\/p>\r\n          <p>\r\n            Our hopes are centred upon the return of Jesus, when he will bring\r\n            everlasting life to the faithful and set up on earth the\r\n            long-promised Kingdom of God, which will be the restored Kingdom of\r\n            Israel under divine leadership.\r\n          <\/p>\r\n          <p class=\"red bold\">\r\n            Unfortunately due to the COVID-19 pandemic and the related\r\n            provincial restrictions on public gatherings and social distancing\r\n            measures, our regular public gatherings are suspended until further\r\n            notice.\r\n          <\/p>\r\n          <p class=\"bold\">\r\n            We will continue to offer a memorial service Sundays at 11AM Pacific\r\n            Time (10AM July-August), provided primarily online, and hope to add\r\n            further events soon. Please see our Events page for more\r\n            information.\r\n          <\/p>'
    , 1
    , 1
    , UTC_TIMESTAMP()
    , UTC_TIMESTAMP()
)
, (
    'Events'
    , '/events'
    , '<h1>Events</h1>\r\n      <p>\r\n        Currently we are conducting some weekly events online with \r\n        <a href=\"https://zoom.us\" target=\"_blank\" rel=\"noopener noreferrer\">\r\n          Zoom\r\n        </a> \r\n        – video/audio conferencing software.\r\n      </p>\r\n\r\n      <p>\r\n        <span class=\"bold\">Mac/Windows/Linux</span>: To install on your\r\n        computer, visit the Zoom \r\n        <a\r\n          href=\"https://zoom.us/download\"\r\n          target=\"_blank\"\r\n          rel=\"noopener noreferrer\"\r\n        >\r\n          download page\r\n        </a> \r\n        and download the “Zoom Client for Meetings.”\r\n      </p>\r\n\r\n      <p>\r\n        <span class=\"bold\">iOS/Android</span>: To install on your\r\n        phone/tablet, visit the App Store (iOS) or the Google Play Store\r\n        (Android) and install the “Zoom Cloud Meetings” app.\r\n      </p>\r\n\r\n      <p class=\"italics\">\r\n        You do not need to sign up for an account, just open the app, and enter\r\n        the Meeting ID to join the meeting.\r\n      </p>\r\n\r\n      <h2>Virtual Events Currently Offered</h2>\r\n      <h3>Memorial Service</h3>\r\n\r\n      <p>\r\n        We currently offer a virtual memorial service via Zoom on Sundays at\r\n        11AM Pacific Time (10AM July-August) to our members, members of other\r\n        Christadelphian churches and interested non-members (please \r\n        <a href=\"/contact\">contact us</a> to say hello and for the Zoom\r\n        meeting ID). Please note the following order of service:\r\n      </p>\r\n\r\n      <ul>\r\n        <li>Welcome</li>\r\n        <li>Hymn</li>\r\n        <li>Prayer</li>\r\n        <li>Announcements</li>\r\n        <li>2 scripture readings</li>\r\n        <li>Exhortation</li>\r\n        <li>\r\n          Bread and Wine\r\n          <br />\r\n          <span class=\"small-note\">\r\n            Please Note: The bread and wine is intended for members of our\r\n            church only. Feel free to reach out and discuss this with us!\r\n          </span>\r\n        </li>\r\n        <li>Hymn</li>\r\n        <li>Closing Prayer</li>\r\n      </ul>\r\n\r\n      <p>\r\n        Note to our members: We are able to accept donations electronically, or\r\n        by mail. <a href=\"/donate\">See details</a>.\r\n      </p>\r\n\r\n      <p>\r\n        Missed the memorial service? Video recordings are \r\n        <a href=\"/audio\">uploaded weekly</a>.\r\n      </p>'
    , 2
    , 1
    , UTC_TIMESTAMP()
    , UTC_TIMESTAMP()
)
, (
    'CYC Conference'
    , '/conference'
    , '<h1>CYC Conference<\/h1>\r\n      <p>\r\n        Unfortunately due to the COVID-19 pandemic and the related restrictions\r\n        on public gatherings and international travel, the 2020 Victoria CYC\r\n        conference is cancelled. If you have paid in advance with PayPal, you\r\n        will receive a refund.\r\n      <\/p>\r\n\r\n      <p>\r\n        If you have any questions or concerns related to this cancellation,\r\n        please contact \r\n        <a href=\"mailto:chase.snobelen@gmail.com\">\r\n          Bro. Chase and Sis. Meagan Snobelen\r\n        <\/a>\r\n        .\r\n      <\/p>'
    , 3
    , 1
    , UTC_TIMESTAMP()
    , UTC_TIMESTAMP()
)
